ALTER TABLE `tbl_enquiries` CHANGE `id` `id` INT(11) NOT NULL AUTO_INCREMENT, CHANGE `name` `name` VARCHAR(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL, CHANGE `email` `email` VARCHAR(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL, CHANGE `message` `message` LONGTEXT CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL, CHANGE `created_at` `created_at` DATETIME NULL DEFAULT CURRENT_TIMESTAMP;

ALTER TABLE `tbl_enquiries` ADD `address` LONGTEXT NULL DEFAULT NULL AFTER `created_at`, ADD `city` VARCHAR(100) NULL DEFAULT NULL AFTER `address`, ADD `state` VARCHAR(100) NULL DEFAULT NULL AFTER `city`, ADD `country` VARCHAR(100) NULL DEFAULT NULL AFTER `state`, ADD `pincode` VARCHAR(100) NULL DEFAULT NULL AFTER `country`;